import React from "react";
import { useState, useEffect } from 'react';


import "./styles.css";
import api from "./services/api";

function App() {
  const [repositories, setResositories] = useState([]);

  useEffect(() => {
    api.get('repositories').then(response => {
      setResositories(response.data);
      console.log(response.data);
    })
  }, []);


  async function handleAddRepository() {
    const response = await api.post('repositories', {
      title: "TRK",
      techs: ["Node.js", "React", "javaScript"]
    });

    const repository = response.data;
    setResositories([...repositories, repository]);
  }

  async function handleRemoveRepository(id) {
    api.delete(`repositories/${id}`).then(response => {
      setResositories(response.data);
      console.log(response.data);
    })
  }

  return (
    <div>
      <ul>
        {repositories.map(repository => (<li key={repository.id}>{repository.title}</li>))}
        {/* {repositories.map(repository => <li key={repository.id}>{repository.title}</li>)} */}
        <button onClick={() => handleRemoveRepository('78e85907-580c-4b77-8217-b754fff3a4dd')}>
          Remover
        </button>
      </ul>

      <button onClick={handleAddRepository}>Adicionar</button>
    </div>
  );
}

export default App;
